// Extract uploaded file from webhook
const files = $input.all()[0].binary;

if (!files || !files.data) {
  throw new Error('No file uploaded. Please upload an image file.');
}

// Get the first uploaded file
const fileKey = Object.keys(files)[0];
const file = files[fileKey];

return {
  json: {
    message: 'File received successfully',
    filename: file.fileName || 'uploaded-image',
    mimeType: file.mimeType
  },
  binary: {
    image: file
  }
};

// Process the API response
const response = items[0].json;

if (response.success) {
  // Decode base64 image
  const imageData = response.data.image;
  const buffer = Buffer.from(imageData, 'base64');

  return [
    {
      json: {
        message: 'Image processed successfully',
        processingTime: response.data.processing_time_seconds,
        action: response.data.action,
        originalFormat: response.data.format,
        downloadUrl: `http://localhost:5000/api/download/${response.data.filename}`
      },
      binary: {
        processedImage: {
          data: buffer,
          mimeType: `image/${response.data.format}`,
          fileName: `processed_${response.metadata.filename}`
        }
      }
    }
  ];
} else {
  throw new Error('Image processing failed: ' + (response.error || 'Unknown error'));
}
